import React,{useState} from 'react';
import{Input, Modal, Popover,Radio} from 'antd'
import {ArrowDownOutlined, DownOutlined, SettingOutlined} from '@ant-design/icons';
import tokenlist from"../tokenList.json";

function Swap() {

  const[slippage,setSlippage]=useState(2.5);
  const[tokenOneAmount,settokenOneAmount]=useState(null);
  const[tokenTwoAmount,settokenTwoAmount]=useState(null);
  const[tokenOne,settokenOne]=useState(tokenlist[0]);
  const[tokenTwo,settokenTwo]=useState(tokenlist[5]);
  const[isOpen,setisOpen]=useState(false);
  const[changeToken,setchangeToken]=useState(1);




  function handleSlippageChange(e){
    setSlippage(e.target.value);
  }
  function changeAmount(e){
    settokenOneAmount(e.target.value);
  }
  function switchTokens(){
    const one=tokenOne;
    console.log(one)
    const two=tokenTwo;
    console.log(two)
    settokenOne(two);
    settokenTwo(one);
  }

  function openModel(asset){
    setchangeToken(asset);
    setIsOpen(true);
  }
  const settings=(
    <>
      <div>Slippage</div>
      <div>
        <Radio.Group value={slippage} onChange={handleSlippageChange}>
          <Radio.Button value={0.5}>0.5</Radio.Button>
          <Radio.Button value={2.5}>2.5</Radio.Button>
          <Radio.Button value={5}>5</Radio.Button>
        </Radio.Group>
      </div>
    </>
  );

  return (
    <>
    <Modal
      open={isOpen}
      footer={null}
      onCancel={()=>setIsOpen(false)}
      title="Select a Token"
    >
    </Modal>
    <div className='tradeBox'>
      <div className='tradeBoxHeader'>
        <h4>Swap</h4>
        <Popover
          content={settings}
          title="Settings"
          trigger="click"
          placement="bottomRight" 
        >
          <SettingOutlined className="cog"/>
        </Popover>
      </div>
      <div className='inputs'>
        <Input placeholder='0' value={tokenOneAmount} onChange={changeAmount}></Input>
        <Input placeholder='0' value={tokenTwoAmount} disabled={true}></Input>
        <div className='switchButton' onClick={switchTokens}>
          <ArrowDownOutlined className='switchArrow'/>
        </div>
        <div className='assetOne' onClick={()=>openModel(1)}>
        <img src={tokenOne.img} alt='assetOneLogo' className='assetLogo'></img>
        {tokenOne.ticker}
        <DownOutlined/>
        </div>
        <div className='assetTwo'onClick={()=>openModel(2)}>
        <img src={tokenTwo.img} alt='assetOneLogo' className='assetLogo'></img>
        {tokenTwo.ticker}
        <DownOutlined/>
        </div>
      </div>
    </div>
    </>
  )
}

export default Swap;
